mapper.select:
select c.num, nickname, subject, content, TO_CHAR(reg_date, 'YYYY-MM-DD') AS reg_date, hitcount, status, user_num, TO_CHAR(sdate, 'YYYY-MM-DD') AS sdate, TO_CHAR(edate, 'YYYY-MM-DD') AS edate, gender, total_people, current_people, estimate_cost from companion c join companion_info ci on c.num=ci.num join companion_age ca on ca.num=c.num WHERE gender=#{gender}
AND age=#{age}
order by reg_date DESC, subject ASC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

mapper.update:

	
		update companion_info set current_people=current_people+1
	
	
		update companion_info set current_people=current_people-1
	

where num=#{num}

mapper.select:
select nvl(count(*), 0)
from (select * from declaration WHERE isreply=1 AND reportpage='companionReply')
WHERE user_num=#{user_num} AND article_num=#{article_num} AND reporter_num=#{reporter_num}

select nvl(count(*), 0)
from (select * from declaration WHERE isreply=0 AND reportpage='companion')
WHERE user_num=#{user_num} AND article_num=#{article_num} AND reporter_num=#{reporter_num}

mapper.insert:
update companion_info set sdate=#{sdate}, edate=#{edate}, total_people=#{total_people},
estimate_cost=#{estimate_cost}, gender=#{gender}
where num=#{num}
