mapper.select:
SELECT NVL(COUNT(*), 0)
from member_companioninfo
where user_num=#{user_num}

SELECT qna_num, userName, subject, content, TO_CHAR(reg_date, 'yyyy-mm-dd') AS reg_date, answer, TO_CHAR(answer_date, 'yyyy-mm-dd') AS answer_date, boardname FROM ( SELECT q.qna_num, m.username AS userName, q.subject, q.content, q.reg_date, q.answer, q.answer_date, 'room_qna' AS boardname FROM room_qna q JOIN member1 m ON q.user_num = m.user_num WHERE q.anonymous = 0 AND q.user_num = #{user_num}
UNION ALL
SELECT t.qna_num, m.username AS userName, t.subject, t.content, t.reg_date, t.answer, t.answer_date, 'tour_qna' AS boardname FROM tour_qna t JOIN member1 m ON t.user_num = m.user_num WHERE t.anonymous = 0 AND t.user_num = #{user_num} ) subquery ORDER BY reg_date DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

SELECT * FROM ( SELECT r.review_num, r.user_num, i.nickname, r.score, r.content, r.num, r.photo, TO_CHAR(r.reg_date, 'YYYY-MM-DD') as reg_date, 'room' AS boardname FROM room_review r JOIN member1 m ON m.user_num = r.user_num JOIN member_info i ON i.user_num = r.user_num WHERE r.user_num = #{user_num}
UNION ALL
SELECT t.review_num, t.user_num, i.nickname, t.score, t.content, t.num, t.photo, TO_CHAR(t.reg_date, 'YYYY-MM-DD') as reg_date, 'tour' AS boardname FROM tour_review t JOIN member1 m ON m.user_num = t.user_num JOIN member_info i ON i.user_num = t.user_num WHERE t.user_num = #{user_num} ) subquery ORDER BY reg_date DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

		SELECT NVL(SUM(cnt), 0) FROM (
        SELECT COUNT(r.num) as cnt
        FROM room_review r
        WHERE r.user_num = #{user_num}

        UNION ALL

        SELECT COUNT(t.num) as cnt
        FROM tour_review t
        WHERE t.user_num = #{user_num}
    )
